{!REQUIRESCRIPT("/soap/ajax/17.0/apex.js")} 
// PayPal recurring billing terminal (for PersonAccounts)
var accountId = '{!Account.Id}';
var contactId = '{!Contact.Id}';
var cidParm = "";
var aidParm = "";
if (contactId == '') {
    contactId = checkForPersonContactId(accountId);
}
if (contactId !== '') {
cidParm = 'cid='+contactId+'&';
}
if (accountId !== '') {
aidParm = 'aid='+accountId+'&';
} 

if (cidParm == "") {
   alert('This button is configured to work with Person Accounts only.  Please navigate to a Contact under this Account and open the terminal from there.');
} else {
window.location.href="/apex/pymt__PayPalARBTerminal?"+cidParm+aidParm+"oid=&cancelURL=%2F"+contactId+"&initialAmount=&amount=&period=&frequency=&occurrences=&invNum=";
}

function checkForPersonContactId(aid) {
    if (aid == null || aid == '') return '';
    try {
        var connection = sforce.connection; 
        var q = "Select Id, IsPersonAccount, PersonContactId from Account where Id = '"+aid+"' and IsPersonAccount = true";
        var queryResult = sforce.connection.query(q); 
        var records = queryResult.getArray('records'); 
        return records[0].get("PersonContactId");
    } catch(err) {
         // PersonAccounts not supported or invalid account Id
    }
    return '';
}
